function ajaxBlocks(o,e){$.ajax({type:"get",url:"/block/ajax-blocks/"+o,beforeSend:function(){$(e).empty(),$(e).append("<option disabled selected> Cargando... </option>")}}).error(function(){alert("Error al obtener los datos\nPor favor, verifique su conexi√≥n a Internet")}).done(function(o){$(e).empty(),$(e).append("<option disabled selected> Seleccione un Block </option>"),$.each(o.blocks,function(o,n){$(e).append('<option value="'+n.id+'">'+n.estacion+"</option>")})})}